import React from 'react';
import {
  Box,
  Drawer,
  DrawerContent,
  DrawerOverlay,
  useDisclosure,
  IconButton,
  Button,
  VStack,
  useBreakpointValue
} from '@chakra-ui/react';
import { FiMenu } from 'react-icons/fi';

const SidebarContent: React.FC = () => (
  <Box
    w={{ base: 200, md: 250 }}
    p="5"
    bg="blue.500"
    color="white"
    height="100vh"
  >
    <VStack spacing={4}>
      <Button w="full">Home</Button>
      <Button w="full">Profile</Button>
      <Button w="full">Settings</Button>
      <Button w="full">Logout</Button>
    </VStack>
  </Box>
);

const Sidebar: React.FC = () => {
  const { isOpen, onOpen, onClose } = useDisclosure();
  const btnRef = React.useRef<HTMLButtonElement>(null);

  // Use Drawer on smaller screens and inline Box on larger screens
  const sidebarDisplay = useBreakpointValue({ base: 'drawer', md: 'box' });

  return (
    <>
      {sidebarDisplay === 'drawer' ? (
        <>
          <IconButton
            ref={btnRef}
            icon={<FiMenu />}
            onClick={onOpen}
            aria-label="Open Menu"
            size="lg"
          />
          <Drawer
            isOpen={isOpen}
            placement="left"
            onClose={onClose}
            finalFocusRef={btnRef}
          >
            <DrawerOverlay />
            <DrawerContent>
              <SidebarContent />
            </DrawerContent>
          </Drawer>
        </>
      ) : (
        <SidebarContent />
      )}
    </>
  );
};

export default Sidebar;
